var canvas = document.getElementById('myCanvas');
var color_arr = new Array("#FFFFFF","#FFBB00","#FF4500","#FF0000","#D02090","#7B68EE","#0000FF","#7FFFD4","#00FF00","#ADFF2F");
var sA = (Math.PI / 180) * 270, // start at the top (270°)
	pA = (Math.PI / 180) * 36,  // one part is 360°/10 = 36°
	dp,	// number of digital place 
	ld, // last digit (array (last digit, in a row) (5999 => last digit = 9,in a row = 3) 
	mp = 350, // middle
	br = 300, // big radius
	sr = 250, // small radius
	nods = GET("nods") ? GET("nods") : 800, // irrat_number of digits
	irrat_name = GET("n") ? GET("n") : "pi",
	show_dots;
var irrat_number,irrat_label;

switch(GET("show_dots")) {
	case "true":
		show_dots = true;
		break;
	case "false":
		show_dots = false;
		sr = Math.floor((sr+br)/2);
		break;
	default:
		show_dots = true;
}
	
if (canvas && canvas.getContext) {
   var ctx = canvas.getContext("2d");
   if (ctx) {
      ctx.fillStyle = "black"; //background color
      ctx.fillRect(0,0,ctx.canvas.width, ctx.canvas.height);
	  
	  ctx.fillStyle = "white";
	  ctx.strokeStyle = "white";
	  ctx.font = "italic 60pt Arial";
	  switch (irrat_name) {
		case "pi":  
			irrat_label = String.fromCharCode(parseInt('03C0', 16)); 
			break;
		case "golden_ratio": 
			irrat_label = String.fromCharCode(parseInt('03A6', 16)); // Phi
			break;		
		case "e":
			irrat_label = "e";
			break;
		default:
			if (irrat_name.substr(0,4) == "sqrt" && parseInt(irrat_name.substr(4)) >=2 && parseInt(irrat_name.substr(4)) <=8 && parseInt(irrat_name.substr(4)) !=4) {
				irrat_label = String.fromCharCode(parseInt('221A', 16))+irrat_name.substr(4);
			} else {
				alert("We don't have this number in our database :/");
				irrat_label = String.fromCharCode(parseInt('03C0', 16)); // using pi instead because pi is awesome :D
				irrat_name = "pi";
			}
	  }
	  
	  ctx.fillText(irrat_label, mp-35,mp+30); // write irrat_label in the "middle"
	
	  for (var i=0; i <= 9; i++) {
		  ctx.strokeStyle = color_arr[i]; // every digit has a color
		  ctx.lineWidth = 5;
		  ctx.beginPath();
		  ctx.arc (mp,  mp,  br,  sA+(i)*pA,  sA+(i+1)*pA,  false);
		  ctx.stroke();
		  ctx.closePath();
		  ctx.fillStyle = "white";
		  ctx.strokeStyle = "white";
		  ctx.font = "italic 30pt Arial";
		  if (i > 4 && i < 8) {
			ctx.fillText(i.toString(), mp+(br+40)*Math.cos(sA+(i+0.5)*pA),mp+(br+40)*Math.sin(sA+(i+0.5)*pA));
		  } else {
			if (i == 3 || i == 4 || i == 8) {
				ctx.fillText(i.toString(), mp+(br+25)*Math.cos(sA+(i+0.5)*pA),mp+(br+25)*Math.sin(sA+(i+0.5)*pA));
			} else {
				ctx.fillText(i.toString(), mp+(br+10)*Math.cos(sA+(i+0.5)*pA),mp+(br+10)*Math.sin(sA+(i+0.5)*pA));
			}
		  }
	  }
	  
	  
	  // supported irrational numbers (first 9999 digits)
	  switch(irrat_name) {
		case "pi" :
			irrat_number = '3.';
			break;
		case "golden_ratio": 
			irrat_number = '1.';
			break;
		case "e": 
			irrat_number = '2.';
	  		break;
		case "sqrt2": 
			irrat_number = '1.';
	  		break;
		case "sqrt3": 
			irrat_number = '1.';
	  		break;
		case "sqrt5": 
			irrat_number = '2.';
	  		break;
		case "sqrt6": 
			irrat_number = '2.';
	  		break;
		case "sqrt7": 
			irrat_number = '2.';
	  		break;
		case "sqrt8": 
			irrat_number = '2.';
	  		break;
	  }
	  
	  ld = new Array('',0); // last digit not set and the sequence has the length 0
	  if (nods >= 10000) { nods = 9999; alert("Showing only 9999 decimal places..."); }
	  for (i = 0; i < nods; i++) {
		  if (i != 1) { // decimal point
			if (i == 0) {
				ld = line(1,parseInt(irrat_number.substr(i,1)),parseInt(irrat_number.substr(2,1)),ld);
			} else {
				ld = line(i,parseInt(irrat_number.substr(i,1)),parseInt(irrat_number.substr(i+1,1)),ld);
			}
		  }
	  }
    }
}

function line(dp,no_1,no_2,ld) {
	if (show_dots) {
		if (no_2 == ld[0]) {
			ld[1]++; // increment in a row
		} else {
			ld[1]++;
			if (ld[1] >= 2) {
				ctx.lineWidth = 1;
				ctx.strokeStyle = color_arr[ld[0]];
				ctx.beginPath();
				ctx.fillStyle = color_arr[ld[0]];
				ctx.arc(mp+((sr+br)/2)*Math.cos(sA+(ld[0]+(dp-ld[1]/2)/nods)*pA), mp+((sr+br)/2)*Math.sin(sA+(ld[0]+(dp-ld[1]/2)/nods)*pA), (ld[1]/15)*((br-sr)/2), 0, 2*Math.PI);
				ctx.fill();
				ctx.stroke();
				ctx.closePath();
			}
			ld[1] = 0;
			ld[0] = no_2;
		}
	}

	var nod_1 = dp/nods; 
	var nod_2 = (dp+1)/nods;
	var quadcurve_int = middle(no_1+nod_1,no_2+nod_2); // get the middle of no_1+nod_1 and no_2+nod_2 (middle of 9 and 0 isn't 4.5 it's 9.5)
	
	var grad = ctx.createLinearGradient(mp+sr*Math.cos(sA+(no_1+nod_1)*pA), mp+sr*Math.sin(sA+(no_1+nod_1)*pA), mp+sr*Math.cos(sA+(no_2+nod_2)*pA), mp+sr*Math.sin(sA+(no_2+nod_2)*pA));
	grad.addColorStop(0, color_arr[no_2]); // can be switched so no_2 = no_1 and no_1 = no_2 but I think this is prettier
	grad.addColorStop(1, color_arr[no_1]);
	
	ctx.lineWidth = 1;
	ctx.strokeStyle = grad;
	ctx.beginPath();
	
	// drawing a curved line from no_1 to no_2
	ctx.moveTo(mp+sr*Math.cos(sA+(no_1+nod_1)*pA), mp+sr*Math.sin(sA+(no_1+nod_1)*pA));
	ctx.quadraticCurveTo(mp+0.7*sr*Math.cos(sA+(quadcurve_int)*pA), mp+0.7*sr*Math.sin(sA+(quadcurve_int)*pA)  ,mp+sr*Math.cos(sA+(no_2+nod_2)*pA),mp+sr*Math.sin(sA+(no_2+nod_2)*pA));
	ctx.stroke();
	return ld;
}



function middle(no_1,no_2) {
	var min = Math.min(no_1,no_2);
	var max = Math.max(no_1,no_2);
	var i = max-min; // counterclockwise 
	var j = 10+Math.floor(min) - Math.floor(max); // clockwise
	if (i < j) {
		return min+i/2;
	} else {
		return max+j/2;
	}
}


function GET(name) {
		var result = (RegExp(name + '=' + '(.+?)(&|$)'). 
		exec(location.search)||[,undefined])[1]; 
		return result;
	}